import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import Dashboard from "./components/Dashboard";
import Machines from "./components/Machines";
import "./App.css";

function App() {
  const [selectedSector, setSelectedSector] = useState("Sector 1"); // Default sector

  return (
    <Router>
      <div className="app">
        <Sidebar setSelectedSector={setSelectedSector} />
        <div className="main-content">
          <Header selectedSector={selectedSector} />
          <Routes>
            <Route path="/" element={<Dashboard selectedSector={selectedSector} />} />
            <Route path="/machines" element={<Machines />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
