import React from "react";
import "./App.css";
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid } from "recharts";
import { FaTemperatureHigh, FaMobile, FaGasPump } from "react-icons/fa";

// Sector Data
const sectors = [
  { name: "Sector 1", machines: 10, issues: 2 },
  { name: "Sector 2", machines: 5, issues: 0 },
  { name: "Sector 3", machines: 8, issues: 1 },
  { name: "Sector 4", machines: 12, issues: 0 },
];

// Machine Data
const machineData = [
  { name: "Machine A", pressure: 1200, temperature: 150, vibration: 5, gasFlow: 2.5, RUL: 30 },
  { name: "Machine B", pressure: 1300, temperature: 200, vibration: 6, gasFlow: 2.8, RUL: 25 },
];

// Dummy Chart Data
const historicalData = [
  { time: "10 AM", value: 1200 },
  { time: "11 AM", value: 1250 },
  { time: "12 PM", value: 1300 },
];

const App = () => {
  return (
    <div className="dashboard">
      <h1>Predictive Maintenance Dashboard</h1>

      {/* Sectors Overview */}
      <div className="grid">
        {sectors.map((sector, index) => (
          <div key={index} className={`card ${sector.issues > 0 ? "error" : ""}`}>
            <h2>{sector.name}</h2>
            <p>Total Machines: {sector.machines}</p>
            {sector.issues > 0 && <p className="error-message">Issues Detected</p>}
          </div>
        ))}
      </div>

      {/* Machine Details */}
      <div className="sector-details">
        <h2>Sector 1 - Machine Data</h2>
        <div className="machine-grid">
          {machineData.map((machine, index) => (
            <div key={index} className="machine-card">
              <h3>{machine.name}</h3>
              <p><FaTemperatureHigh /> Temperature: {machine.temperature}Â°C</p>
              <p><FaVibration /> Vibration: {machine.vibration} Hz</p>
              <p><FaGasPump /> Gas Flow: {machine.gasFlow} kg/s</p>
              <p>Pressure: {machine.pressure} psi</p>
              <p>Remaining Useful Life (RUL): {machine.RUL} hours</p>

              <div className={`status ${machine.pressure < 1000 || machine.pressure > 1500 ? "error" : ""}`}>
                Pressure Status: {machine.pressure < 1000 || machine.pressure > 1500 ? "Abnormal" : "Normal"}
              </div>

              {/* Historical Data Chart */}
              <LineChart width={300} height={100} data={historicalData}>
                <XAxis dataKey="time" />
                <YAxis />
                <Tooltip />
                <CartesianGrid strokeDasharray="3 3" />
                <Line type="monotone" dataKey="value" stroke="#007bff" />
              </LineChart>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default App;
