import React from "react";
import { NavLink } from "react-router-dom";
import "../styles/Sidebar.css";

const sectors = [
  { name: "Sector 1", machines: 10 },
  { name: "Sector 2", machines: 8 },
  { name: "Sector 3", machines: 12 },
];

const Sidebar = ({ setSelectedSector }) => (
  <div className="sidebar">
    <h2>ALL SECTORS</h2>
    <ul>
      {sectors.map((sector, index) => (
        <li key={index} onClick={() => setSelectedSector(sector.name)} className="sector-item">
          <a href="#">{sector.name}</a>
          <span className="machine-count">{sector.machines} Machines</span>
        </li>
      ))}
    </ul>

    <nav className="navigation">
      <NavLink to="/" className={({ isActive }) => (isActive ? "active" : "")}>DASHBOARD</NavLink>
      <NavLink to="/machines" className={({ isActive }) => (isActive ? "active" : "")}>MACHINES</NavLink>
    </nav>
  </div>
);

export default Sidebar;
