import React from "react";
import { AlertCircle, Thermometer, Activity, Wind, TrendingUp } from "lucide-react";
import "../styles/Dashboard.css";

const allMachines = [
  { sector: "Sector 1", name: "Machine 1", pressure: 1200, temperature: 250, vibration: 5, gasFlow: 2.5, RUL: 80 },
  { sector: "Sector 1", name: "Machine 2", pressure: 1600, temperature: 310, vibration: 8, gasFlow: 3.5, RUL: 40 },
  { sector: "Sector 2", name: "Machine 3", pressure: 1100, temperature: 200, vibration: 3, gasFlow: 2.2, RUL: 95 },
  { sector: "Sector 2", name: "Machine 4", pressure: 1300, temperature: 270, vibration: 6, gasFlow: 2.8, RUL: 70 },
  { sector: "Sector 3", name: "Machine 5", pressure: 1400, temperature: 290, vibration: 7, gasFlow: 3.0, RUL: 60 },
  { sector: "Sector 3", name: "Machine 6", pressure: 1500, temperature: 300, vibration: 8, gasFlow: 3.2, RUL: 50 },
];

const isAbnormal = (machine) => {
  return (
    machine.pressure < 1000 || machine.pressure > 1500 ||
    machine.temperature < 100 || machine.temperature > 300 ||
    machine.vibration < 0 || machine.vibration > 10 ||
    machine.gasFlow < 2 || machine.gasFlow > 3
  );
};

const Dashboard = ({ selectedSector }) => {
  const filteredMachines = allMachines.filter(machine => machine.sector === selectedSector);

  return (
    <div className="dashboard">
      <h2>{selectedSector} - Machines</h2>
      <div className="total-machines">
        <h3>Total Machines</h3>
        <div className="total-number">{filteredMachines.length}</div>
      </div>

      <div className="machine-grid">
        {filteredMachines.map((machine, index) => (
          <div key={index} className={`machine-card ${isAbnormal(machine) ? 'error' : 'good'}`}>
            <h2>{machine.name}</h2>
            <div className="machine-data">
              <div className="data-item">
                <TrendingUp className="icon blue-icon" />
                <span>{machine.pressure} psi</span>
              </div>
              <div className="data-item">
                <Thermometer className="icon red-icon" />
                <span>{machine.temperature}Â°C</span>
              </div>
              <div className="data-item">
                <Activity className="icon purple-icon" />
                <span>{machine.vibration} Hz</span>
              </div>
              <div className="data-item">
                <Wind className="icon green-icon" />
                <span>{machine.gasFlow} kg/s</span>
              </div>
            </div>
            <div className="rul">
              <span>RUL: {machine.RUL}%</span>
              {isAbnormal(machine) && <AlertCircle className="alert-icon" />}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
